import React from 'react';
import './Home.css';

import { withRouter } from 'react-router-dom';
import { withFirebase } from '../Firebase';


const HomePage = () => (
    <div>
        <HomeForm />
    </div>
);

const INITIAL_STATE = {
    name: '',
    email: '',
    phone:'',
    error: null,
};

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = { INITIAL_STATE };
    }

    render() {

        return (
            <div className="container">
                <div className="col-md-6 mx-auto text-center">
                    <div className="header-title">
                        <h1 className="wv-heading--title">
                            Cadastro
            </h1>
                        <h2 className="wv-heading--subtitle">
                            Adicione um novo funcion√°rio
            </h2>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-4 mx-auto">
                        <div className="myform form ">
                            <form >
                                <div className="form-group">
                                    <input type="text" name="name" className="form-control my-input" id="name" placeholder="Name" />
                                </div>
                                <div className="form-group">
                                    <input type="email" name="email" className="form-control my-input" id="email" placeholder="Email" />
                                </div>
                                <div className="form-group">
                                    <input type="number" min="0" name="phone" id="phone" className="form-control my-input" placeholder="Phone" />
                                </div>
                                <div className="text-center ">
                                    <button className="btn btn-lg btn-primary btn-block" type="submit">Cadastrar</button>
                                </div>

                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const HomeForm = withRouter(withFirebase(Home));

export default HomePage;
export { HomeForm };
